import React from "react";
import "./SearchBar.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { withRouter } from "react-router-dom";

class SearchBar extends React.Component {
  state = {
    value: null
  };

  handlePress = event => {
    const key = event.key;
    const value = encodeURI(event.target.value);
    return key === "Enter"
      ? this.props.history.push(`/searchpage/results?search_query=${value}`)
      : null
  };

  onChange = event => {
    this.setState({ value: this.props.query });
  };

  render() {
    return (
      <div className="container-1">
        <div className="container-2">
          <span className="search-icon">
            <FontAwesomeIcon icon={faSearch} className="fa fa-search" />
          </span>
          <input
            name="value"
            onKeyPress={this.handlePress}
            value={this.state.value}
            onChange={this.onChange}
            type="search"
            id="search"
            placeholder="Planets, Asteroids, Stars..."
          />
        </div>
      </div>
    );
  }
}

export default withRouter(SearchBar);
