import ApodActionTypes from "./apod-actions";

const INITIAL_STATE = {
  isFetching: false,
  apod: [],
  errorMessage: undefined
};

const apodReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ApodActionTypes.FETCH_APOD_DATA_START: {
      return { ...state, isFetching: true };
    }
    case ApodActionTypes.FETCH_APOD_DATA_SUCCESS: {
      return { ...state, isFetching: false, apod: action.payload };
    }
    case ApodActionTypes.FETCH_APOD_DATA_ERROR: {
      return { ...state, isFetching: false, errorMessage: action.payload };
    }
    default: {
      return state;
    }
  }
};

export default apodReducer;
