import { createSelector } from "reselect";

const selectSearch = state => state.search;

export const selectSearchData = createSelector(
  [selectSearch],
  search => search.searchData ? search.searchData : []
);

export const selectSearchImageById = (searchId) => createSelector(
  [selectSearchData],
  searchData => searchData.filter(({data}) => data[0].nasa_id === searchId )
);

export const selectIsImageSearching = createSelector(
  [selectSearch],
  search => search.isFetching
);

export const isKeyPressed = createSelector(
  [selectSearch],
  search => search.isKeyPressed

);
