import React from "react";
import "./SearchPage.scss";
import SearchBar from "../../Components/SearchBar/SearchBar";
import SearchImageOverview from "../../Components/SearchImageOverview/SearchImageOverview";
import queryString from "query-string";
import fetchSearchDataAsync from "../../Redux/Search/search-actions";
import { connect } from "react-redux";

class SearchPage extends React.Component {
 state = {
   value: null
 }

  componentDidMount() {
    const values = queryString.parse(this.props.location.search);
    const value = encodeURI(values.search_query);
    this.setState({ value: value })
    const { fetchSearchDataAsync } = this.props;
    if (typeof value !== 'undefined') {
      fetchSearchDataAsync(value)
    }
  }

  render() {
    return (
      <div className="searchpage">
        <span className="searchpage__title">
          Search For Images In NASA Database
        </span>
        <SearchBar query={this.state.value} />
        <SearchImageOverview />
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  fetchSearchDataAsync: value => dispatch(fetchSearchDataAsync(value))
});

export default connect(null, mapDispatchToProps)(SearchPage);
