import React from "react";
import "./SearchPage.scss";
import SearchBar from "../../Components/SearchBar/SearchBar";
import SearchImageOverview from "../../Components/SearchImageOverview/SearchImageOverview";
import queryString from "query-string";
import fetchSearchDataAsync from "../../Redux/Search/search-actions";
import { connect } from "react-redux";
import {selectSearchData} from '../../Redux/Search/search-selectors';

class SearchPage extends React.Component {
  componentDidMount() {
    const { search } = this.props.location;
    const values = queryString.parse(search);
    const value = encodeURI(values.search_query);
    console.log(value);
    const { fetchSearchDataAsync } = this.props;
    fetchSearchDataAsync(value);
  }

  render() {
    return (
      <div className="searchpage">
        <span className="searchpage__title">
          Search For Images In NASA Database
        </span>
        <SearchBar />
        <SearchImageOverview />
      </div>
    );
  } 
}

const mapStateToProps = state => ({ 
  searchData: selectSearchData(state)
});

export default connect(mapStateToProps)(SearchPage);
