import React from "react";
import "./Apod.scss";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectApodData } from "../../Redux/Apod/apod-selectors";
import getApodAsync from "../../Redux/Apod/apod-actions";

class Apod extends React.Component {
  componentDidMount() {
    const {getApodAsync} = this.props;
    getApodAsync();
  }
  render() {
    const { apodData } = this.props;
    const { title, url, explanation } = apodData;
    return (
      <div className="apod">
        <span className="apod__title">Astronomical Picture Of The Day</span>
        <img src={url} alt="apod" className="apod__image" />
        <span className="apod__name">{title}</span>
        <span className="apod__description">{explanation}</span>
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  apodData: selectApodData
});

const mapDispatchToProps = dispatch => ({
  getApodAsync: () => dispatch(getApodAsync())
});

export default connect(mapStateToProps, mapDispatchToProps)(Apod);
