import React from "react";
import "./SearchBar.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import fetchSearchDataAsync from '../../Redux/Search/search-actions';
import {connect} from 'react-redux';
import { persistStore } from 'redux-persist';

const SearchBar = ({fetchSearchDataAsync}) => {

  const handlechange = event => {
    const value = event.target.value;
    return value ? fetchSearchDataAsync(value) : persistStore().purge()
  }

  return (
    <div className="container-1">
      <div className="container-2">
        <span className="search-icon">
          <FontAwesomeIcon icon={faSearch} className="fa fa-search" />
        </span>
        <input
          onChange={handlechange}
          type="search"
          id="search"
          placeholder="Planets, Asteroids, Stars..."
        />
      </div>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  fetchSearchDataAsync: value => dispatch(fetchSearchDataAsync(value))
});

export default connect(null, mapDispatchToProps)(SearchBar);
