import React from "react";
import "./SearchBar.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { withRouter } from "react-router-dom";
import {setKeyPressed} from "../../Redux/Search/search-actions";
import { connect } from "react-redux";



class SearchBar extends React.Component {

  handlePress = event => {

    const key = event.key;
    const value = encodeURI(event.target.value);
    return key === "Enter"
      ? this.props.history.push(`/searchpage/results?search_query=${value}`)
      : null
  };

  render() {
    return (
      <div className="container-1">
        <div className="container-2">
          <span className="search-icon">
            <FontAwesomeIcon icon={faSearch} className="fa fa-search" />
          </span>
          <input
            name="value"
            onKeyPress={this.handlePress}
            type="search"
            id="search"
            placeholder="Planets, Asteroids, Stars..."
          />
        </div>
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  setKeyPressed: () => dispatch(setKeyPressed())
});

export default connect(null, mapDispatchToProps)(withRouter(SearchBar));
