import React from 'react';

class SignIn extends React.Component {
    constructor() {
      super();

      this.state = {
          email: '',
          password: ''
      }
    }

    handleSubmit = async event => {
        event.preventDefault();
        const { email, password } = this.state;

        try {
          await auth.signInWithEmailAndPassword(email, password);
          this.setState({ email: "", password: "" });
        } catch (error) {
          console.error(error);
        }
      };

      handleChange = event => {
        const { value, name } = event.target;

        this.setState({ [name]: value });
      };

  render() {
      return (
        <div className="signin">
        <div className="signin__bg"/>
        <div className="signin__container">
          <div className="signin__shadow">
            <h1 className="signin__title">Sign In</h1>

            <form
              action="POST"
              autoComplete="new-password"
              onSubmit={this.handleSubmit}
            >
              <FormInput
                name="email"
                type="email"
                value={this.state.email}
                handleChange={this.handleChange}
                label="Email"
                required
              />

              <FormInput
                name="password"
                type="password"
                value={this.state.password}
                handleChange={this.handleChange}
                label="Password"
                required
              />

              <div className="signin__btn-container">
                <div className="signin__btn">
                  <CustomButton type="submit" signin>
                    {" "}
                    Sign In{" "}
                  </CustomButton>
                </div>
              </div>
            </form>
            <div className="signin__option">
              <span className="signin__option--newuser">New to Nasa?</span>
              <Link to="/signup" className="signin__option--link">
                Sign up now.
              </Link>
            </div>
          </div>
        </div>
      </div>

      );
  }
} 

export default SignIn;
